import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { checkAndImportPeptides } from "./auto-import";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // AUTOMATIC PEPTIDE IMPORT: Check and import peptides if database is empty
  console.log('üöÄ Server starting...');
  console.log('üìä Checking peptide database...');

  try {
    const imported = await checkAndImportPeptides();
    if (imported) {
      console.log('üéØ Peptides imported successfully!');
    } else {
      console.log('‚úì Peptides already exist or import skipped.');
    }
  } catch (error) {
    console.error('‚ö†Ô∏è  Warning: Failed to check/import peptides:', error);
    console.error('The server will continue without peptides data.');
  }

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPeptideSchema, insertCategorySchema, insertProtocolSchema, insertGuideSchema } from "@shared/schema";
import { z } from "zod";
import { sanitizeAndNormalizeContent } from "./content-sanitizer";

export async function registerRoutes(app: Express): Promise<Server> {
  // Peptide endpoints

  // GET /api/peptides - List all peptides
  app.get("/api/peptides", async (req, res) => {
    try {
      const peptides = await storage.getAllPeptides();
      res.json(peptides);
    } catch (error) {
      console.error("Error fetching peptides:", error);
      res.status(500).json({ error: "Failed to fetch peptides" });
    }
  });

  // GET /api/peptides/search - Search peptides
  app.get("/api/peptides/search", async (req, res) => {
    try {
      const query = req.query.q as string;
      if (!query) {
        return res.status(400).json({ error: "Search query is required" });
      }

      const peptides = await storage.searchPeptides(query);
      res.json(peptides);
    } catch (error) {
      console.error("Error searching peptides:", error);
      res.status(500).json({ error: "Failed to search peptides" });
    }
  });

  // GET /api/peptides/:id - Get single peptide
  app.get("/api/peptides/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const peptide = await storage.getPeptide(id);

      if (!peptide) {
        return res.status(404).json({ error: "Peptide not found" });
      }

      res.json(peptide);
    } catch (error) {
      console.error("Error fetching peptide:", error);
      res.status(500).json({ error: "Failed to fetch peptide" });
    }
  });

  // POST /api/peptides - Create peptide
  app.post("/api/peptides", async (req, res) => {
    try {
      const validated = insertPeptideSchema.parse(req.body);
      const peptide = await storage.createPeptide(validated);
      res.status(201).json(peptide);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid peptide data", details: error.errors });
      }
      console.error("Error creating peptide:", error);
      res.status(500).json({ error: "Failed to create peptide" });
    }
  });

  // PATCH /api/peptides/:id - Update peptide
  app.patch("/api/peptides/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const partialValidated = insertPeptideSchema.partial().parse(req.body);
      const updated = await storage.updatePeptide(id, partialValidated);

      if (!updated) {
        return res.status(404).json({ error: "Peptide not found" });
      }

      res.json(updated);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid peptide data", details: error.errors });
      }
      console.error("Error updating peptide:", error);
      res.status(500).json({ error: "Failed to update peptide" });
    }
  });

  // DELETE /api/peptides/:id - Delete peptide
  app.delete("/api/peptides/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deletePeptide(id);

      if (!deleted) {
        return res.status(404).json({ error: "Peptide not found" });
      }

      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting peptide:", error);
      res.status(500).json({ error: "Failed to delete peptide" });
    }
  });

  // Category endpoints

  // GET /api/categories - List all categories
  app.get("/api/categories", async (req, res) => {
    try {
      const categories = await storage.getAllCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ error: "Failed to fetch categories" });
    }
  });

  // POST /api/categories - Create category
  app.post("/api/categories", async (req, res) => {
    try {
      const validated = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(validated);
      res.status(201).json(category);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid category data", details: error.errors });
      }
      console.error("Error creating category:", error);
      res.status(500).json({ error: "Failed to create category" });
    }
  });

  // Protocol endpoints

  // GET /api/protocols - List all protocols
  app.get("/api/protocols", async (req, res) => {
    try {
      const protocols = await storage.getAllProtocols();
      res.json(protocols);
    } catch (error) {
      console.error("Error fetching protocols:", error);
      res.status(500).json({ error: "Failed to fetch protocols" });
    }
  });

  // GET /api/protocols/:id - Get single protocol
  app.get("/api/protocols/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const protocol = await storage.getProtocol(id);

      if (!protocol) {
        return res.status(404).json({ error: "Protocol not found" });
      }

      res.json(protocol);
    } catch (error) {
      console.error("Error fetching protocol:", error);
      res.status(500).json({ error: "Failed to fetch protocol" });
    }
  });

  // POST /api/protocols - Create protocol
  app.post("/api/protocols", async (req, res) => {
    try {
      const validated = insertProtocolSchema.parse(req.body);
      const protocol = await storage.createProtocol(validated);
      res.status(201).json(protocol);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid protocol data", details: error.errors });
      }
      console.error("Error creating protocol:", error);
      res.status(500).json({ error: "Failed to create protocol" });
    }
  });

  // Guide endpoints

  // GET /api/guides - List guides with efficient pagination
  app.get("/api/guides", async (req, res) => {
    try {
      const page = parseInt(req.query.page as string) || 1;
      const limit = parseInt(req.query.limit as string) || 10;
      const offset = (page - 1) * limit;

      const result = await storage.getGuidesPaginated(limit, offset);

      // For list view, skip expensive content sanitization (only needed for individual article view)
      const guides = result.data;

      res.json({
        data: guides,
        page,
        limit,
        total: result.total,
        totalPages: Math.ceil(result.total / limit)
      });
    } catch (error) {
      console.error("Error fetching guides:", error);
      res.status(500).json({ error: "Failed to fetch guides" });
    }
  });

  // GET /api/guides/featured - Get featured guides
  app.get("/api/guides/featured", async (req, res) => {
    try {
      const guides = await storage.getFeaturedGuides();

      // Sanitize content for featured guides
      const sanitizedGuides = guides.map(guide => ({
        ...guide,
        content: sanitizeAndNormalizeContent(guide.content, true)
      }));

      res.json(sanitizedGuides);
    } catch (error) {
      console.error("Error fetching featured guides:", error);
      res.status(500).json({ error: "Failed to fetch featured guides" });
    }
  });

  // GET /api/guides/search - Search guides
  app.get("/api/guides/search", async (req, res) => {
    try {
      const query = req.query.q as string;
      if (!query) {
        return res.status(400).json({ error: "Search query is required" });
      }

      const guides = await storage.searchGuides(query);

      // For search results, skip expensive sanitization (only needed for individual article view)
      res.json(guides);
    } catch (error) {
      console.error("Error searching guides:", error);
      res.status(500).json({ error: "Failed to search guides" });
    }
  });

  // GET /api/guides/category/:category - Get guides by category
  app.get("/api/guides/category/:category", async (req, res) => {
    try {
      const { category } = req.params;
      const guides = await storage.getGuidesByCategory(category);

      // Sanitize content for guides by category
      const sanitizedGuides = guides.map(guide => ({
        ...guide,
        content: sanitizeAndNormalizeContent(guide.content, true)
      }));

      res.json(sanitizedGuides);
    } catch (error) {
      console.error("Error fetching guides by category:", error);
      res.status(500).json({ error: "Failed to fetch guides by category" });
    }
  });

  // GET /api/guides/:slug - Get single guide by slug
  app.get("/api/guides/:slug", async (req, res) => {
    try {
      const { slug } = req.params;
      const guide = await storage.getGuideBySlug(slug);

      if (!guide) {
        return res.status(404).json({ error: "Guide not found" });
      }

      // Sanitize the content before returning
      const sanitizedGuide = {
        ...guide,
        content: sanitizeAndNormalizeContent(guide.content, true)
      };

      res.json(sanitizedGuide);
    } catch (error) {
      console.error("Error fetching guide:", error);
      res.status(500).json({ error: "Failed to fetch guide" });
    }
  });

  // POST /api/guides - Create guide
  app.post("/api/guides", async (req, res) => {
    try {
      const validated = insertGuideSchema.parse(req.body);
      const guide = await storage.createGuide(validated);
      res.status(201).json(guide);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid guide data", details: error.errors });
      }
      console.error("Error creating guide:", error);
      res.status(500).json({ error: "Failed to create guide" });
    }
  });

  // PUT /api/guides/:id - Update guide
  app.put("/api/guides/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const partialValidated = insertGuideSchema.partial().parse(req.body);
      const updated = await storage.updateGuide(id, partialValidated);

      if (!updated) {
        return res.status(404).json({ error: "Guide not found" });
      }

      res.json(updated);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: "Invalid guide data", details: error.errors });
      }
      console.error("Error updating guide:", error);
      res.status(500).json({ error: "Failed to update guide" });
    }
  });

  // DELETE /api/guides/:id - Delete guide
  app.delete("/api/guides/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const deleted = await storage.deleteGuide(id);

      if (!deleted) {
        return res.status(404).json({ error: "Guide not found" });
      }

      res.json({ success: true });
    } catch (error) {
      console.error("Error deleting guide:", error);
      res.status(500).json({ error: "Failed to delete guide" });
    }
  });

  // Sitemap endpoint for SEO
  app.get("/sitemap.xml", async (req, res) => {
    try {
      const baseUrl = process.env.NODE_ENV === 'production' 
        ? 'https://peptide-dojo.replit.app' 
        : 'http://localhost:5000';

      // Get all content for sitemap
      const [peptides, guides] = await Promise.all([
        storage.getAllPeptides(),
        storage.getAllGuides()
      ]);

      const currentDate = new Date().toISOString().split('T')[0];

      // Static pages with priority and change frequency
      const staticPages = [
        { url: '/', priority: '1.0', changefreq: 'daily', lastmod: currentDate },
        { url: '/peptides', priority: '0.9', changefreq: 'daily', lastmod: currentDate },
        { url: '/research', priority: '0.9', changefreq: 'daily', lastmod: currentDate }
      ];

      // Dynamic peptide pages
      const peptidePages = peptides.map(peptide => ({
        url: `/peptide/${peptide.id}`,
        priority: '0.8',
        changefreq: 'weekly',
        lastmod: currentDate
      }));

      // Dynamic guide pages  
      const guidePages = guides.map(guide => ({
        url: `/guide/${guide.slug}`,
        priority: '0.8', 
        changefreq: 'weekly',
        lastmod: new Date(guide.publishDate).toISOString().split('T')[0]
      }));

      // Combine all pages
      const allPages = [...staticPages, ...peptidePages, ...guidePages];

      // Generate XML sitemap
      const sitemap = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
${allPages.map(page => `  <url>
    <loc>${baseUrl}${page.url}</loc>
    <lastmod>${page.lastmod}</lastmod>
    <changefreq>${page.changefreq}</changefreq>
    <priority>${page.priority}</priority>
  </url>`).join('\n')}
</urlset>`;

      res.set('Content-Type', 'application/xml');
      res.send(sitemap);
    } catch (error) {
      console.error("Error generating sitemap:", error);
      res.status(500).json({ error: "Failed to generate sitemap" });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Peptide, 
  type InsertPeptide,
  type Category,
  type InsertCategory,
  type Protocol,
  type InsertProtocol,
  type Guide,
  type InsertGuide,
  users,
  peptides,
  categories,
  protocols,
  guides
} from "@shared/schema";
import { sql } from "drizzle-orm";
import { randomUUID } from "crypto";
import { db } from "./db";
import { eq, ilike, or, desc, asc } from "drizzle-orm";

// Extended interface with all CRUD methods
export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Peptide operations
  createPeptide(peptide: InsertPeptide): Promise<Peptide>;
  getPeptide(id: string): Promise<Peptide | undefined>;
  getAllPeptides(): Promise<Peptide[]>;
  updatePeptide(id: string, peptide: Partial<InsertPeptide>): Promise<Peptide | undefined>;
  deletePeptide(id: string): Promise<boolean>;
  searchPeptides(query: string): Promise<Peptide[]>;

  // Category operations
  createCategory(category: InsertCategory): Promise<Category>;
  getAllCategories(): Promise<Category[]>;

  // Protocol operations
  createProtocol(protocol: InsertProtocol): Promise<Protocol>;
  getAllProtocols(): Promise<Protocol[]>;
  getProtocol(id: string): Promise<Protocol | undefined>;

  // Guide operations
  createGuide(guide: InsertGuide): Promise<Guide>;
  getGuide(id: string): Promise<Guide | undefined>;
  getGuideBySlug(slug: string): Promise<Guide | undefined>;
  getAllGuides(): Promise<Guide[]>;
  updateGuide(id: string, guide: Partial<InsertGuide>): Promise<Guide | undefined>;
  deleteGuide(id: string): Promise<boolean>;
  getGuidesByCategory(category: string): Promise<Guide[]>;
  getFeaturedGuides(): Promise<Guide[]>;
  searchGuides(query: string): Promise<Guide[]>;
  getGuidesPaginated(limit?: number, offset?: number): Promise<{data: Guide[], total: number}>;
}

// In-memory storage implementation
export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private peptides: Map<string, Peptide>;
  private categories: Map<string, Category>;
  private protocols: Map<string, Protocol>;
  private guides: Map<string, Guide>;

  constructor() {
    this.users = new Map();
    this.peptides = new Map();
    this.categories = new Map();
    this.protocols = new Map();
    this.guides = new Map();
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  // Peptide operations
  async createPeptide(insertPeptide: InsertPeptide): Promise<Peptide> {
    const id = randomUUID();
    const peptide: Peptide = { 
      ...insertPeptide, 
      id,
      alternateNames: insertPeptide.alternateNames || null,
      ingredients: insertPeptide.ingredients || null,
      categoryId: insertPeptide.categoryId || null,
      shortDescription: insertPeptide.shortDescription || null,
      description: insertPeptide.description || null,
      dosage: insertPeptide.dosage || null,
      researchApplications: insertPeptide.researchApplications || null,
    } as Peptide;
    this.peptides.set(id, peptide);
    return peptide;
  }

  async getPeptide(id: string): Promise<Peptide | undefined> {
    return this.peptides.get(id);
  }

  async getAllPeptides(): Promise<Peptide[]> {
    return Array.from(this.peptides.values());
  }

  async updatePeptide(id: string, peptideData: Partial<InsertPeptide>): Promise<Peptide | undefined> {
    const existing = this.peptides.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...peptideData };
    this.peptides.set(id, updated);
    return updated;
  }

  async deletePeptide(id: string): Promise<boolean> {
    return this.peptides.delete(id);
  }

  async searchPeptides(query: string): Promise<Peptide[]> {
    const lowercaseQuery = query.toLowerCase();
    return Array.from(this.peptides.values()).filter(
      (peptide) => 
        peptide.name.toLowerCase().includes(lowercaseQuery) ||
        (peptide.description && peptide.description.toLowerCase().includes(lowercaseQuery)) ||
        (peptide.shortDescription && peptide.shortDescription.toLowerCase().includes(lowercaseQuery))
    );
  }

  // Category operations
  async createCategory(insertCategory: InsertCategory): Promise<Category> {
    const id = randomUUID();
    const category: Category = { 
      ...insertCategory, 
      id,
      description: insertCategory.description || null
    } as Category;
    this.categories.set(id, category);
    return category;
  }

  async getAllCategories(): Promise<Category[]> {
    return Array.from(this.categories.values());
  }

  // Protocol operations
  async createProtocol(insertProtocol: InsertProtocol): Promise<Protocol> {
    const id = randomUUID();
    const protocol: Protocol = { 
      ...insertProtocol, 
      id,
      description: insertProtocol.description || null,
      peptides: insertProtocol.peptides || null,
      duration: insertProtocol.duration || null,
      instructions: insertProtocol.instructions || null
    } as Protocol;
    this.protocols.set(id, protocol);
    return protocol;
  }

  async getAllProtocols(): Promise<Protocol[]> {
    return Array.from(this.protocols.values());
  }

  async getProtocol(id: string): Promise<Protocol | undefined> {
    return this.protocols.get(id);
  }

  // Guide operations
  async createGuide(insertGuide: InsertGuide): Promise<Guide> {
    const id = randomUUID();
    const now = new Date();
    const guide: Guide = { 
      ...insertGuide, 
      id,
      excerpt: insertGuide.excerpt || null,
      tags: insertGuide.tags || null,
      relatedPeptides: insertGuide.relatedPeptides || null,
      keywords: insertGuide.keywords || null,
      publishDate: now,
      lastUpdated: now
    } as Guide;
    this.guides.set(id, guide);
    return guide;
  }

  async getGuide(id: string): Promise<Guide | undefined> {
    return this.guides.get(id);
  }

  async getGuideBySlug(slug: string): Promise<Guide | undefined> {
    return Array.from(this.guides.values()).find(
      (guide) => guide.slug === slug
    );
  }

  async getAllGuides(): Promise<Guide[]> {
    return Array.from(this.guides.values());
  }

  async updateGuide(id: string, guideData: Partial<InsertGuide>): Promise<Guide | undefined> {
    const existing = this.guides.get(id);
    if (!existing) return undefined;

    const updated = { 
      ...existing, 
      ...guideData,
      lastUpdated: new Date()
    };
    this.guides.set(id, updated);
    return updated;
  }

  async deleteGuide(id: string): Promise<boolean> {
    return this.guides.delete(id);
  }

  async getGuidesByCategory(category: string): Promise<Guide[]> {
    return Array.from(this.guides.values()).filter(
      (guide) => guide.category === category
    );
  }

  async getFeaturedGuides(): Promise<Guide[]> {
    return Array.from(this.guides.values()).filter(
      (guide) => guide.featured === true
    );
  }

  async searchGuides(query: string): Promise<Guide[]> {
    const lowercaseQuery = query.toLowerCase();
    return Array.from(this.guides.values()).filter(
      (guide) => 
        guide.title.toLowerCase().includes(lowercaseQuery) ||
        guide.content.toLowerCase().includes(lowercaseQuery) ||
        guide.metaDescription.toLowerCase().includes(lowercaseQuery) ||
        (guide.excerpt && guide.excerpt.toLowerCase().includes(lowercaseQuery)) ||
        (guide.tags && guide.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery))) ||
        (guide.keywords && guide.keywords.some(keyword => keyword.toLowerCase().includes(lowercaseQuery)))
    );
  }

  async getGuidesPaginated(limit: number = 10, offset: number = 0): Promise<{data: Guide[], total: number}> {
    const allGuides = Array.from(this.guides.values());
    const sortedGuides = allGuides.sort((a, b) => new Date(b.publishDate).getTime() - new Date(a.publishDate).getTime());
    const paginatedGuides = sortedGuides.slice(offset, offset + limit);

    return {
      data: paginatedGuides,
      total: allGuides.length
    };
  }
}

// Database storage implementation using Drizzle ORM
export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, id))
      .limit(1);
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.username, username))
      .limit(1);
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  // Peptide operations
  async createPeptide(insertPeptide: InsertPeptide): Promise<Peptide> {
    const [peptide] = await db
      .insert(peptides)
      .values(insertPeptide)
      .returning();
    return peptide;
  }

  async getPeptide(id: string): Promise<Peptide | undefined> {
    const [peptide] = await db
      .select()
      .from(peptides)
      .where(eq(peptides.id, id))
      .limit(1);
    return peptide;
  }

  async getAllPeptides(): Promise<Peptide[]> {
    return await db.select().from(peptides);
  }

  async updatePeptide(id: string, peptideData: Partial<InsertPeptide>): Promise<Peptide | undefined> {
    const [updated] = await db
      .update(peptides)
      .set(peptideData)
      .where(eq(peptides.id, id))
      .returning();
    return updated;
  }

  async deletePeptide(id: string): Promise<boolean> {
    const result = await db
      .delete(peptides)
      .where(eq(peptides.id, id))
      .returning();
    return result.length > 0;
  }

  async searchPeptides(query: string): Promise<Peptide[]> {
    return await db
      .select()
      .from(peptides)
      .where(
        or(
          ilike(peptides.name, `%${query}%`),
          ilike(peptides.description, `%${query}%`),
          ilike(peptides.shortDescription, `%${query}%`)
        )
      );
  }

  // Category operations
  async createCategory(insertCategory: InsertCategory): Promise<Category> {
    const [category] = await db
      .insert(categories)
      .values(insertCategory)
      .returning();
    return category;
  }

  async getAllCategories(): Promise<Category[]> {
    return await db.select().from(categories);
  }

  // Protocol operations
  async createProtocol(insertProtocol: InsertProtocol): Promise<Protocol> {
    const [protocol] = await db
      .insert(protocols)
      .values(insertProtocol)
      .returning();
    return protocol;
  }

  async getAllProtocols(): Promise<Protocol[]> {
    return await db.select().from(protocols);
  }

  async getProtocol(id: string): Promise<Protocol | undefined> {
    const [protocol] = await db
      .select()
      .from(protocols)
      .where(eq(protocols.id, id))
      .limit(1);
    return protocol;
  }

  // Guide operations
  async createGuide(insertGuide: InsertGuide): Promise<Guide> {
    const [guide] = await db
      .insert(guides)
      .values(insertGuide)
      .returning();
    return guide;
  }

  async getGuide(id: string): Promise<Guide | undefined> {
    const [guide] = await db
      .select()
      .from(guides)
      .where(eq(guides.id, id))
      .limit(1);
    return guide;
  }

  async getGuideBySlug(slug: string): Promise<Guide | undefined> {
    const [guide] = await db
      .select()
      .from(guides)
      .where(eq(guides.slug, slug))
      .limit(1);
    return guide;
  }

  async getAllGuides(): Promise<Guide[]> {
    return await db
      .select()
      .from(guides)
      .orderBy(desc(guides.publishDate));
  }

  async getGuidesPaginated(limit: number = 10, offset: number = 0): Promise<{data: Guide[], total: number}> {
    const [guidesResult, countResult] = await Promise.all([
      db
        .select()
        .from(guides)
        .orderBy(desc(guides.publishDate))
        .limit(limit)
        .offset(offset),
      db
        .select({ count: sql<number>`count(*)` })
        .from(guides)
    ]);

    return {
      data: guidesResult,
      total: countResult[0].count
    };
  }

  async updateGuide(id: string, guideData: Partial<InsertGuide>): Promise<Guide | undefined> {
    const [updated] = await db
      .update(guides)
      .set({
        ...guideData,
        lastUpdated: new Date()
      })
      .where(eq(guides.id, id))
      .returning();
    return updated;
  }

  async deleteGuide(id: string): Promise<boolean> {
    const result = await db
      .delete(guides)
      .where(eq(guides.id, id))
      .returning();
    return result.length > 0;
  }

  async getGuidesByCategory(category: string): Promise<Guide[]> {
    return await db
      .select()
      .from(guides)
      .where(eq(guides.category, category))
      .orderBy(desc(guides.publishDate));
  }

  async getFeaturedGuides(): Promise<Guide[]> {
    return await db
      .select()
      .from(guides)
      .where(eq(guides.featured, true))
      .orderBy(desc(guides.publishDate));
  }

  async searchGuides(query: string): Promise<Guide[]> {
    return await db
      .select()
      .from(guides)
      .where(
        or(
          ilike(guides.title, `%${query}%`),
          ilike(guides.content, `%${query}%`),
          ilike(guides.metaDescription, `%${query}%`),
          ilike(guides.excerpt, `%${query}%`)
        )
      )
      .orderBy(desc(guides.publishDate));
  }
}

// Use DatabaseStorage when DATABASE_URL is present, otherwise use MemStorage
export const storage: IStorage = process.env.DATABASE_URL 
  ? new DatabaseStorage()
  : new MemStorage();

