import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

export interface ModuleCardProps {
  title: string;
  description: string;
  category: "performance" | "recovery" | "wellness" | "longevity" | "weight-loss" | "blends" | "best-sellers" | "drive";
  duration: string;
  difficulty: "Beginner" | "Intermediate" | "Advanced";
  isCompleted: boolean;
  isLocked: boolean;
  onStart?: () => void;
}

export default function ModuleCard({
  title,
  description,
  category,
  duration,
  difficulty,
  isCompleted,
  isLocked,
  onStart
}: ModuleCardProps) {
  // Map categories to their corresponding icons from brand assets
  const categoryIcons = {
    "performance": "‚ö°",
    "recovery": "üîÑ", 
    "wellness": "üõ°Ô∏è",
    "longevity": "‚è≥",
    "weight-loss": "‚öñÔ∏è",
    "blends": "üß™",
    "best-sellers": "üèÜ",
    "drive": "‚ù§Ô∏è"
  };

  // Use semantic classes for difficulty levels
  const getDifficultyClass = (difficulty: string) => {
    switch(difficulty) {
      case "Beginner":
        return "bg-primary/10 text-primary border-primary/20";
      case "Intermediate":
        return "bg-accent text-accent-foreground border-accent/20";
      case "Advanced":
        return "bg-destructive/10 text-destructive border-destructive/20";
      default:
        return "bg-muted text-muted-foreground border-muted";
    }
  };

  return (
    <Card className="relative overflow-hidden hover-elevate transition-all duration-300 h-full" data-testid={`card-module-${title.toLowerCase().replace(/\s+/g, '-')}`}>
      {/* Ghost pattern background texture */}
      <div 
        className="absolute inset-0 opacity-3"
        style={{
          backgroundImage: `url('/src/assets/patterns/LRG_Pattern_On White_Ghost_1758946478158.png')`,
          backgroundSize: '150px 150px',
          backgroundRepeat: 'repeat'
        }}
      ></div>

      {/* Lock overlay for locked modules */}
      {isLocked && (
        <div className="absolute inset-0 bg-background/80 z-10 flex items-center justify-center">
          <div className="text-center">
            <div className="text-4xl mb-2">üîí</div>
            <p className="text-muted-foreground font-medium">Complete previous modules</p>
          </div>
        </div>
      )}

      <CardHeader className="relative pb-4">
        <div className="flex items-center justify-between mb-2">
          <Badge 
            variant="outline" 
            className={`${getDifficultyClass(difficulty)} font-medium`}
            data-testid={`badge-difficulty-${difficulty.toLowerCase()}`}
          >
            {difficulty}
          </Badge>
          <div className="text-2xl" data-testid={`icon-category-${category}`}>
            {categoryIcons[category]}
          </div>
        </div>
        <CardTitle className="text-lg font-bold leading-tight" data-testid={`title-module-${title.toLowerCase().replace(/\s+/g, '-')}`}>
          {title}
        </CardTitle>
      </CardHeader>

      <CardContent className="relative">
        <p className="text-muted-foreground mb-4 text-sm leading-relaxed min-h-[60px]" data-testid={`text-description-${title.toLowerCase().replace(/\s+/g, '-')}`}>
          {description}
        </p>

        <div className="flex items-center justify-between mb-4 text-sm text-muted-foreground">
          <span data-testid={`text-duration-${duration.replace(/\s+/g, '-')}`}>
            üìö {duration}
          </span>
          <span className="capitalize" data-testid={`text-category-${category}`}>
            üè∑Ô∏è {category.replace('-', ' ')}
          </span>
        </div>

        <Button
          variant={isCompleted ? "secondary" : "default"}
          disabled={isLocked}
          onClick={onStart}
          className="w-full font-medium"
          data-testid={`button-start-${title.toLowerCase().replace(/\s+/g, '-')}`}
        >
          {isCompleted ? "Review Module" : "Start Learning"}
        </Button>
      </CardContent>
    </Card>
  );
}

client/src/components/Navigation.tsx.content:
import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { useIsMobile } from "@/hooks/use-mobile";
import { Menu, X } from "lucide-react";
import shrineLogoPath from "@assets/LogoV_Black_v2_1758948960878.jpg";
import shrineIconPath from "@assets/Icon_White_1758953368901.png";
import { cn } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";

export default function Navigation({ onLoginClick }: { onLoginClick?: () => void }) {
  const [location] = useLocation();
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const isMobile = useIsMobile();
  const { toast } = useToast();

  const navItems = [
    { path: "/peptides", label: "Peptide Information" },
    { path: "/research", label: "Peptide Research" }
  ];

  const handleNavClick = (path: string, label: string) => {
    // Only show coming soon for non-peptide and non-research routes
    if (path !== "/peptides" && path !== "/research") {
      console.log(`Navigation to ${label}: Coming soon`);
      toast({
        title: "Coming Soon",
        description: `The ${label} section is currently under development.`,
      });
    }
  };

  // Detect scroll position
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      setIsScrolled(scrollPosition > 10);
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll(); // Check initial scroll position

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Close mobile menu when route changes
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  return (
    <nav 
      className={cn(
        "sticky top-0 z-[100] transition-all duration-300 ease-sacred bg-black border-b border-white/10",
        isScrolled 
          ? "shadow-xl shadow-black/30" 
          : "shadow-lg shadow-black/20"
      )}
      role="navigation"
      aria-label="Main navigation"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="h-20 md:h-24 lg:h-28 flex items-center justify-between">
          {/* Logo */}
          <Link 
            href="/" 
            className="flex items-center space-x-3 sm:space-x-4 hover-elevate py-2 px-3 rounded-md transition-sacred min-h-[56px] min-w-[56px]"
            aria-label="Peptide Dojo Home"
            data-testid="logo-home-link"
          >
            <img 
              src={shrineIconPath} 
              alt="Peptide Dojo - Evidence-based peptide research platform" 
              className="h-12 w-12 sm:h-14 sm:w-14 md:h-16 md:w-16 object-contain"
              width="64"
              height="64"
              data-testid="logo-shrine-icon"
            />
            <div className="font-bold text-xl sm:text-2xl md:text-3xl tracking-tight">
              <span className="text-white">PEPTIDE</span>
              <span className="text-primary ml-1">DOJO</span>
            </div>
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-2 lg:space-x-3">
            {navItems.map((item) => (
              (item.path === "/peptides" || item.path === "/research") ? (
                <Link key={item.path} href={item.path}>
                  <Button
                    variant="ghost"
                    size="lg"
