import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

export interface TopicCardProps {
  title: string;
  summary: string;
  category: "peptide" | "condition" | "guide" | "research";
  evidenceGrade: "A" | "B" | "C" | "D" | "Insufficient";
  lastUpdated: string;
  studyCount?: number;
  href: string;
  onView?: () => void;
}

export default function TopicCard({
  title,
  summary,
  category,
  evidenceGrade,
  lastUpdated,
  studyCount,
  href,
  onView
}: TopicCardProps) {
  // Use semantic classes for evidence grading
  const getEvidenceClass = (grade: string) => {
    switch(grade) {
      case "A": 
        // Strong evidence - use primary/success colors
        return "bg-primary/10 text-primary border-primary/20";
      case "B":
        // Good evidence - use secondary colors  
        return "bg-secondary/80 text-secondary-foreground border-secondary/20";
      case "C":
        // Moderate evidence - use accent colors
        return "bg-accent text-accent-foreground border-accent/20";
      case "D":
        // Weak evidence - use muted colors
        return "bg-muted text-muted-foreground border-muted";
      case "Insufficient":
        // No evidence - use very muted colors
        return "bg-muted/50 text-muted-foreground border-muted/50";
      default:
        return "bg-muted text-muted-foreground border-muted";
    }
  };

  const categoryLabels = {
    "peptide": "Peptide",
    "condition": "Condition",
    "guide": "Guide",
    "research": "Research"
  };

  return (
    <Card className="h-full hover-elevate transition-all duration-200" data-testid={`card-topic-${title.toLowerCase().replace(/\s+/g, '-')}`}>
      <CardHeader className="pb-3 sm:pb-4">
        <div className="flex flex-wrap items-start justify-between gap-2 mb-2">
          <Badge variant="outline" className="text-xs sm:text-xs" data-testid={`badge-category-${category}`}>
            {categoryLabels[category]}
          </Badge>
          <Badge 
            variant="outline"
            className={`text-xs font-medium ${getEvidenceClass(evidenceGrade)}`}
            data-testid={`badge-evidence-${evidenceGrade.toLowerCase()}`}
          >
            Grade {evidenceGrade}
          </Badge>
        </div>
        <CardTitle className="text-base sm:text-lg font-bold leading-tight" data-testid={`title-${title.toLowerCase().replace(/\s+/g, '-')}`}>
          {title}
        </CardTitle>
      </CardHeader>

      <CardContent className="pt-0">
        <p className="text-xs sm:text-sm text-muted-foreground mb-3 sm:mb-4 leading-relaxed min-h-[3rem] sm:min-h-[60px] line-clamp-3" data-testid={`text-summary-${title.toLowerCase().replace(/\s+/g, '-')}`}>
          {summary}
        </p>

        <div className="flex flex-wrap items-center justify-between gap-2 text-xs text-muted-foreground mb-3 sm:mb-4">
          <span data-testid={`text-updated-${lastUpdated.replace(/\s+/g, '-')}`}>
            Updated {lastUpdated}
          </span>
          {studyCount && (
            <span data-testid={`text-studies-${studyCount}`}>
              {studyCount} studies
            </span>
          )}
        </div>

        <Button
          variant="outline"
          size="default"
          className="w-full font-medium"
          onClick={onView}
          data-testid={`button-view-${title.toLowerCase().replace(/\s+/g, '-')}`}
        >
          View Details
        </Button>
      </CardContent>
    </Card>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

