import { useRoute, Link } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { ArrowLeft, ExternalLink } from "lucide-react";
import PageMeta from "@/components/PageMeta";
import type { Peptide, Category } from "@shared/schema";

export default function PeptideDetail() {
  const [, params] = useRoute("/peptide/:id");
  const peptideId = params?.id;

  // Fetch single peptide
  const { data: peptide, isLoading: peptideLoading } = useQuery<Peptide>({
    queryKey: ['/api/peptides', peptideId],
    enabled: !!peptideId,
  });

  // Fetch categories for category display
  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
  });

  const category = categories.find((c) => c.id === peptide?.categoryId);
  const isLoading = peptideLoading;

  if (isLoading) {
    return (
      <div className="min-h-screen py-8 sm:py-12 md:py-16">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
          <Card>
            <CardHeader>
              <Skeleton className="h-8 w-48 mb-4" />
              <Skeleton className="h-6 w-24 mb-2" />
              <Skeleton className="h-10 w-32" />
            </CardHeader>
            <CardContent className="space-y-6">
              <div>
                <Skeleton className="h-6 w-32 mb-2" />
                <Skeleton className="h-4 w-full mb-2" />
                <Skeleton className="h-4 w-full mb-2" />
                <Skeleton className="h-4 w-3/4" />
              </div>
              <div>
                <Skeleton className="h-6 w-32 mb-2" />
                <Skeleton className="h-4 w-full mb-2" />
                <Skeleton className="h-4 w-5/6" />
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!peptide) {
    return (
      <div className="min-h-screen py-8 sm:py-12 md:py-16">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-2xl font-bold mb-4">Peptide Not Found</h1>
          <p className="text-muted-foreground mb-8">
            The peptide you're looking for could not be found.
          </p>
          <Link href="/peptides">
            <Button>
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Peptides
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  // Generate dynamic SEO meta tags
  const pageTitle = `${peptide.name} - Research Peptide | Peptide Dojo`;
  const pageDescription = peptide.shortDescription 
    ? peptide.shortDescription.substring(0, 155) + '...' 
    : `${peptide.name} research peptide. ${peptide.description ? peptide.description.substring(0, 120) + '...' : 'Detailed information, dosing, and research applications.'}`;

  // Schema.org structured data for individual peptide
  const peptideSchema = {
    "@context": "https://schema.org",
    "@type": "Product",
    "name": peptide.name,
    "description": peptide.shortDescription || peptide.description || `${peptide.name} research peptide`,
    "sku": peptide.sku,
    "category": category?.name || "Research Peptides",
    "url": `${window.location.origin}/peptide/${peptide.id}`,
    "offers": {
      "@type": "Offer",
      "price": peptide.price,
      "priceCurrency": "USD",
      "availability": "https://schema.org/InStock",
      "seller": {
        "@type": "Organization",
        "name": "Shrine Peptides",
        "url": "https://shrinepeptides.com"
      }
    },
    "manufacturer": {
      "@type": "Organization",
      "name": "Shrine Peptides",
      "url": "https://shrinepeptides.com"
    },
    "breadcrumb": {
      "@type": "BreadcrumbList",
      "itemListElement": [
        {
          "@type": "ListItem",
          "position": 1,
          "name": "Home",
          "item": window.location.origin
        },
        {
          "@type": "ListItem",
          "position": 2,
          "name": "Peptides",
          "item": `${window.location.origin}/peptides`
        },
        {
          "@type": "ListItem",
          "position": 3,
          "name": peptide.name,
          "item": `${window.location.origin}/peptide/${peptide.id}`
        }
      ]
    }
  };

  return (
    <>
      <PageMeta 
        title={pageTitle}
        description={pageDescription}
        url={`${window.location.origin}/peptide/${peptide.id}`}
        type="product"
        keywords={[peptide.name, ...(peptide.alternateNames || []), 'research peptide', 'peptide therapy', category?.name || 'therapeutic peptides']}
        schema={peptideSchema}
      />
      <div className="min-h-screen py-8 sm:py-12 md:py-16">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Back Navigation */}
        <Link href="/peptides">
          <Button variant="ghost" className="mb-6" data-testid="button-back">
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Peptides
          </Button>
        </Link>

        <Card>
          <CardHeader>
            <div className="flex flex-wrap items-start justify-between gap-4">
              <div className="flex-1">
                <CardTitle className="text-2xl sm:text-3xl font-bold mb-2" data-testid="text-peptide-name">
                  {peptide.name}
                </CardTitle>
                {peptide.alternateNames && peptide.alternateNames.length > 0 && (
                  <p className="text-sm text-muted-foreground mb-3" data-testid="text-alternate-names">
                    Also known as: {peptide.alternateNames.join(", ")}
                  </p>
                )}
                <div className="flex flex-wrap gap-2">
                  {category && (
                    <Badge variant="outline" data-testid="badge-category">
                      {category.name}
                    </Badge>
                  )}
                  {peptide.isBlend && (
                    <Badge className="bg-primary/10 text-primary" data-testid="badge-blend">
                      Peptide Blend
                    </Badge>
                  )}
                  <Badge variant="secondary" data-testid="text-sku">
                    SKU: {peptide.sku}
                  </Badge>
                </div>
              </div>
              <div className="text-right">
                <p className="text-3xl font-bold text-primary" data-testid="text-price">
                  ${peptide.price}
                </p>
              </div>
            </div>
          </CardHeader>

          <CardContent className="space-y-6">
            {/* Short Description */}
            {peptide.shortDescription && (
              <div>
                <h3 className="text-lg font-semibold mb-2" data-testid="heading-overview">Overview</h3>
                <p className="text-muted-foreground leading-relaxed" data-testid="text-overview">
                  {peptide.shortDescription}
                </p>
              </div>
            )}

            {/* Full Description */}
            {peptide.description && (
              <div>
                <h3 className="text-lg font-semibold mb-2" data-testid="heading-description">Detailed Description</h3>
                <p className="text-muted-foreground leading-relaxed whitespace-pre-wrap" data-testid="text-description">
                  {peptide.description}
                </p>
              </div>
            )}

            {/* Dosage Information */}
            {peptide.dosage && (
              <div>
                <h3 className="text-lg font-semibold mb-2" data-testid="heading-dosage">Dosage Information</h3>
                <p className="text-muted-foreground leading-relaxed" data-testid="text-dosage">
                  {peptide.dosage}
                </p>
              </div>
            )}

            {/* Ingredients (for blends) */}
            {peptide.isBlend && peptide.ingredients && peptide.ingredients.length > 0 && (
              <div>
                <h3 className="text-lg font-semibold mb-2" data-testid="heading-ingredients">Blend Ingredients</h3>
                <ul className="list-disc list-inside text-muted-foreground space-y-1" data-testid="list-ingredients">
                  {peptide.ingredients.map((ingredient, index) => (
                    <li key={index} data-testid={`text-ingredient-${index}`}>{ingredient}</li>
                  ))}
                </ul>
              </div>
            )}

            {/* Research Applications */}
            {peptide.researchApplications && (
              <div>
                <h3 className="text-lg font-semibold mb-2" data-testid="heading-research">Research Applications</h3>
                <p className="text-muted-foreground leading-relaxed" data-testid="text-research">
                  {peptide.researchApplications}
                </p>
              </div>
            )}

            {/* Action Buttons */}
            <div className="pt-6 border-t">
              <a
                href={`https://shrinepeptides.com/product/${peptide.sku}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block"
              >
                <Button data-testid="button-view-shrine">
                  View on Shrine Peptides
                  <ExternalLink className="w-4 h-4 ml-2" />
                </Button>
              </a>
            </div>
          </CardContent>
        </Card>
      </div>
      </div>
    </>
  );
}

client/src/pages/Peptides.tsx.content:
import { useState, useMemo, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link, useLocation, useSearch } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { Search } from "lucide-react";
import PageMeta from "@/components/PageMeta";
import type { Peptide, Category } from "@shared/schema";

export default function Peptides() {
  const [, navigate] = useLocation();
  const searchParams = useSearch();
  const urlSearchQuery = new URLSearchParams(searchParams).get('search') || '';
  const urlCategory = new URLSearchParams(searchParams).get('category') || null;

  const [searchQuery, setSearchQuery] = useState(urlSearchQuery);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(urlCategory);

  // Update local state when URL changes
  useEffect(() => {
    setSearchQuery(urlSearchQuery);
  }, [urlSearchQuery]);

  useEffect(() => {
    setSelectedCategory(urlCategory);
  }, [urlCategory]);

  // Update URL when search changes
  const updateSearch = (query: string) => {
    setSearchQuery(query);
    const params = new URLSearchParams();
    if (query) {
      params.set('search', query);
    }
    if (selectedCategory) {
      params.set('category', selectedCategory);
    }
    const queryString = params.toString();
    navigate(`/peptides${queryString ? `?${queryString}` : ''}`);
  };

  // Update category and URL
  const updateCategory = (categoryId: string | null) => {
    setSelectedCategory(categoryId);
    const params = new URLSearchParams();
    if (searchQuery) {
      params.set('search', searchQuery);
    }
    if (categoryId) {
      params.set('category', categoryId);
    }
    const queryString = params.toString();
    navigate(`/peptides${queryString ? `?${queryString}` : ''}`);
  };

  // Fetch all peptides
  const { data: peptides = [], isLoading: peptideLoading } = useQuery<Peptide[]>({
    queryKey: ['/api/peptides'],
  });

  // Fetch all categories
  const { data: categories = [], isLoading: categoryLoading } = useQuery<Category[]>({
    queryKey: ['/api/categories'],
  });

  // Filter peptides based on search and category
  const filteredPeptides = useMemo(() => {
    let filtered = peptides;

    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (peptide) =>
          peptide.name.toLowerCase().includes(query) ||
          (peptide.shortDescription && peptide.shortDescription.toLowerCase().includes(query)) ||
          (peptide.description && peptide.description.toLowerCase().includes(query))
      );
    }

    // Filter by category
    if (selectedCategory) {
      filtered = filtered.filter(
        (peptide) => peptide.categoryId === selectedCategory
      );
    }

    return filtered;
  }, [peptides, searchQuery, selectedCategory]);

  // Get peptide counts per category
  const categoryCounts = useMemo(() => {
    const counts = new Map<string, number>();
    peptides.forEach((peptide) => {
      if (peptide.categoryId) {
        counts.set(peptide.categoryId, (counts.get(peptide.categoryId) || 0) + 1);
      }
    });
    return counts;
  }, [peptides]);

  const isLoading = peptideLoading || categoryLoading;

  // Generate dynamic title and description based on actual data
  const totalPeptides = peptides.length;
  const totalCategories = categories.length;
  const title = `Browse Peptides - ${totalPeptides} Research Peptides | Peptide Dojo`;
  const description = `Explore our comprehensive catalog of ${totalPeptides} research peptides across ${totalCategories} therapeutic categories. Detailed information on peptide applications, dosing protocols, and clinical research data.`;

  // Schema.org structured data for peptides catalog
  const peptidesPageSchema = {
    "@context": "https://schema.org",
    "@type": "CollectionPage",
    "name": "Peptide Library",
    "description": description,
    "url": `${window.location.origin}/peptides`,
    "mainEntity": {
      "@type": "ItemList",
      "numberOfItems": totalPeptides,
      "name": "Research Peptides",
      "description": "Comprehensive collection of research peptides with clinical applications"
    },
    "breadcrumb": {
      "@type": "BreadcrumbList",
      "itemListElement": [
        {
          "@type": "ListItem",
          "position": 1,
          "name": "Home",
          "item": window.location.origin
        },
        {
          "@type": "ListItem", 
          "position": 2,
          "name": "Peptides",
          "item": `${window.location.origin}/peptides`
        }
      ]
    }
  };

  return (
    <>
      <PageMeta 
        title={title}
        description={description}
        url={`${window.location.origin}/peptides${searchQuery || selectedCategory ? '?' + new URLSearchParams(window.location.search).toString() : ''}`}
        type="website"
        keywords={['research peptides', 'peptide therapy', 'therapeutic peptides', 'peptide catalog', 'clinical applications', 'dosing protocols']}
        schema={peptidesPageSchema}
      />
      <div className="min-h-screen py-8 sm:py-12 md:py-16">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8 sm:mb-10 md:mb-12">
          <h1 className="text-3xl sm:text-4xl font-bold mb-3 sm:mb-4">Peptide Library</h1>
          <p className="text-base sm:text-lg font-medium text-muted-foreground max-w-3xl mx-auto px-2 sm:px-0">
            Explore our comprehensive collection of research peptides with detailed information and scientific data.
          </p>
        </div>

        {/* Search Bar */}
        <div className="mb-6 sm:mb-8">
          <div className="relative max-w-xl mx-auto">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-5 h-5" />
            <Input
              type="text"
              placeholder="Search peptides by name or description..."
              value={searchQuery}
              onChange={(e) => updateSearch(e.target.value)}
              className="pl-10 h-12"
              data-testid="input-search-peptides"
            />
          </div>
        </div>

        {/* Category Filters */}
        <div className="mb-8 sm:mb-10">
          <div className="flex flex-wrap items-center justify-center gap-2 sm:gap-3">
            <Button
              variant={selectedCategory === null ? "default" : "outline"}
              size="sm"
              onClick={() => updateCategory(null)}
              data-testid="button-filter-all"
            >
              All Peptides ({peptides.length})
            </Button>
            {categories.map((category) => (
              <Button
                key={category.id}
                variant={selectedCategory === category.id ? "default" : "outline"}
                size="sm"
                onClick={() => updateCategory(category.id)}
                data-testid={`button-filter-${category.slug}`}
              >
                {category.name} ({categoryCounts.get(category.id) || 0})
              </Button>
            ))}
          </div>
        </div>

        {/* Peptides Grid */}
        {isLoading ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-5 md:gap-6">
            {[...Array(8)].map((_, i) => (
              <Card key={i} className="h-full">
                <CardHeader className="pb-3 sm:pb-4">
                  <Skeleton className="h-5 w-20 mb-2" />
                  <Skeleton className="h-6 w-full" />
                </CardHeader>
                <CardContent className="pt-0">
                  <Skeleton className="h-4 w-full mb-2" />
                  <Skeleton className="h-4 w-3/4 mb-4" />
                  <Skeleton className="h-10 w-full" />
                </CardContent>
              </Card>
            ))}
          </div>
        ) : filteredPeptides.length === 0 ? (
          <div className="text-center py-16">
            <p className="text-lg text-muted-foreground">
              {searchQuery || selectedCategory
                ? "No peptides found matching your criteria."
                : "No peptides available."}
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-5 md:gap-6">
            {filteredPeptides.map((peptide) => {
              const category = categories.find((c) => c.id === peptide.categoryId);
              return (
                <Card key={peptide.id} className="h-full hover-elevate transition-all duration-200" data-testid={`card-peptide-${peptide.id}`}>
                  <CardHeader className="pb-3 sm:pb-4">
                    <div className="flex items-start justify-between gap-2 mb-2">
                      {category && (
                        <Badge variant="outline" className="text-xs" data-testid={`badge-category-${peptide.id}`}>
                          {category.name}
                        </Badge>
                      )}
                      {peptide.isBlend && (
                        <Badge className="text-xs bg-primary/10 text-primary" data-testid={`badge-blend-${peptide.id}`}>
                          Blend
                        </Badge>
                      )}
                    </div>
                    <CardTitle className="text-base sm:text-lg font-bold leading-tight" data-testid={`text-peptide-name-${peptide.id}`}>
                      {peptide.name}
                    </CardTitle>
                    {peptide.alternateNames && peptide.alternateNames.length > 0 && (
                      <p className="text-xs text-muted-foreground mt-1" data-testid={`text-alternate-names-${peptide.id}`}>
                        Also known as: {peptide.alternateNames.join(", ")}
                      </p>
                    )}
                  </CardHeader>
                  <CardContent className="pt-0">
                    <p className="text-xs sm:text-sm text-muted-foreground mb-4 leading-relaxed line-clamp-3" data-testid={`text-description-${peptide.id}`}>
                      {peptide.shortDescription || peptide.description || "Research peptide"}
                    </p>
                    <div className="flex items-center justify-between mb-4">
                      <span className="text-lg font-semibold text-primary" data-testid={`text-price-${peptide.id}`}>
                        ${peptide.price}
                      </span>
                      <span className="text-xs text-muted-foreground" data-testid={`text-sku-${peptide.id}`}>
                        SKU: {peptide.sku}
                      </span>
                    </div>
                    <Link href={`/peptide/${peptide.id}`}>
                      <Button
                        variant="outline"
                        size="sm"
                        className="w-full font-medium min-h-[44px]"
                        data-testid={`button-view-${peptide.sku.toLowerCase()}`}
                      >
                        View Details
                      </Button>
                    </Link>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        )}
      </div>
      </div>
    </>
  );
}

