import { useState, useMemo, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation, useSearch } from "wouter";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Pagination,
  PaginationContent,
  PaginationEllipsis,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious,
} from "@/components/ui/pagination";
import { Search, BookOpen } from "lucide-react";
import PageMeta from "@/components/PageMeta";
import GuideCard from "@/components/GuideCard";
import type { Guide } from "@shared/schema";

interface GuideResponse {
  data: Guide[];
  page: number;
  limit: number;
  total: number;
  totalPages: number;
}

export default function Guides() {
  const [, navigate] = useLocation();
  const searchParams = useSearch();
  const urlSearchQuery = new URLSearchParams(searchParams).get('search') || '';
  const urlCategory = new URLSearchParams(searchParams).get('category') || null;
  const urlPage = parseInt(new URLSearchParams(searchParams).get('page') || '1');

  const [searchQuery, setSearchQuery] = useState(urlSearchQuery);
  const [selectedCategory, setSelectedCategory] = useState<string | null>(urlCategory);
  const [currentPage, setCurrentPage] = useState(urlPage);
  const [localSearch, setLocalSearch] = useState(urlSearchQuery);

  const ITEMS_PER_PAGE = 12;

  // Update local state when URL changes
  useEffect(() => {
    setSearchQuery(urlSearchQuery);
    setLocalSearch(urlSearchQuery);
  }, [urlSearchQuery]);

  useEffect(() => {
    setSelectedCategory(urlCategory);
  }, [urlCategory]);

  useEffect(() => {
    setCurrentPage(urlPage);
  }, [urlPage]);

  // Update URL parameters
  const updateUrl = (search?: string, category?: string | null, page?: number) => {
    const params = new URLSearchParams();
    const finalSearch = search !== undefined ? search : searchQuery;
    const finalCategory = category !== undefined ? category : selectedCategory;
    const finalPage = page !== undefined ? page : currentPage;

    if (finalSearch) params.set('search', finalSearch);
    if (finalCategory) params.set('category', finalCategory);
    if (finalPage > 1) params.set('page', String(finalPage));

    const queryString = params.toString();
    navigate(`/research${queryString ? `?${queryString}` : ''}`);
  };

  // Debounced search
  useEffect(() => {
    const timer = setTimeout(() => {
      if (localSearch !== searchQuery) {
        setSearchQuery(localSearch);
        setCurrentPage(1); 
        updateUrl(localSearch, selectedCategory, 1);
      }
    }, 300);
    return () => clearTimeout(timer);
  }, [localSearch]);

  // Update category
  const handleCategoryChange = (category: string | null) => {
    setSelectedCategory(category);
    setCurrentPage(1);
    updateUrl(searchQuery, category, 1);
  };

  // Update page
  const handlePageChange = (page: number) => {
    setCurrentPage(page);
    updateUrl(searchQuery, selectedCategory, page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Fetch guides with reasonable pagination for better performance
  const { data: guidesResponse, isLoading } = useQuery<GuideResponse>({
    queryKey: ['/api/guides'],
    queryFn: async () => {
      const response = await fetch(`/api/guides?limit=50`); // Fetch reasonable amount for good UX
      if (!response.ok) throw new Error('Failed to fetch guides');
      return response.json();
    },
  });

  // Filter guides locally based on search and category
  const filteredGuides = useMemo(() => {
    if (!guidesResponse?.data) return [];

    let filtered = guidesResponse.data;

    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (guide) =>
          guide.title.toLowerCase().includes(query) ||
          (guide.excerpt && guide.excerpt.toLowerCase().includes(query)) ||
          (guide.metaDescription && guide.metaDescription.toLowerCase().includes(query)) ||
          (guide.tags && guide.tags.some(tag => tag.toLowerCase().includes(query)))
      );
    }

    // Filter by category
    if (selectedCategory) {
      filtered = filtered.filter(
        (guide) => guide.category === selectedCategory
      );
    }

    return filtered;
  }, [guidesResponse?.data, searchQuery, selectedCategory]);

  // Get unique categories from guides
  const categories = useMemo(() => {
    if (!guidesResponse?.data) return [];
    const uniqueCategories = new Set(guidesResponse.data.map(guide => guide.category));
    return Array.from(uniqueCategories).sort();
  }, [guidesResponse?.data]);

  // Category counts
  const categoryCounts = useMemo(() => {
    if (!guidesResponse?.data) return new Map();
    const counts = new Map<string, number>();
    guidesResponse.data.forEach(guide => {
      counts.set(guide.category, (counts.get(guide.category) || 0) + 1);
    });
    return counts;
  }, [guidesResponse?.data]);

  // Calculate total pages for filtered results
  const totalFilteredPages = Math.ceil(filteredGuides.length / ITEMS_PER_PAGE);
  const paginatedGuides = filteredGuides.slice(
    (currentPage - 1) * ITEMS_PER_PAGE,
    currentPage * ITEMS_PER_PAGE
  );

  // Create a proper response object for filtered results
  const finalGuidesResponse = {
    data: paginatedGuides,
    page: currentPage,
    limit: ITEMS_PER_PAGE,
    total: filteredGuides.length,
    totalPages: totalFilteredPages
  };

  // Generate dynamic SEO meta
  const totalGuides = guidesResponse?.total || 0;
  const title = `Peptide Research - ${totalGuides} Research Articles | Peptide Dojo`;
  const description = `Explore ${totalGuides} comprehensive research articles covering peptide studies, protocols, and therapeutic applications. Expert-written content with scientific references and clinical insights.`;

  // Schema.org structured data for research articles listing
  const researchPageSchema = {
    "@context": "https://schema.org",
    "@type": "CollectionPage",
    "name": "Peptide Research Articles",
    "description": description,
    "url": `${window.location.origin}/research`,
    "mainEntity": {
      "@type": "ItemList",
      "numberOfItems": totalGuides,
      "name": "Research Articles",
      "description": "Evidence-based peptide research articles and clinical protocols"
    },
    "breadcrumb": {
      "@type": "BreadcrumbList",
      "itemListElement": [
        {
          "@type": "ListItem",
          "position": 1,
          "name": "Home",
          "item": window.location.origin
        },
        {
          "@type": "ListItem", 
          "position": 2,
          "name": "Research",
          "item": `${window.location.origin}/research`
        }
      ]
    }
  };

  return (
    <>
      <PageMeta 
        title={title}
        description={description}
        url={`${window.location.origin}/research${searchQuery || selectedCategory ? '?' + new URLSearchParams(window.location.search).toString() : ''}`}
        type="website"
        keywords={['peptide research', 'clinical protocols', 'dosing guides', 'evidence-based', 'therapeutic peptides', 'research articles', 'peptide studies']}
        schema={researchPageSchema}
      />
      <div className="min-h-screen py-8 sm:py-12 md:py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Header */}
          <div className="text-center mb-8 sm:mb-10 md:mb-12">
            <div className="flex items-center justify-center gap-3 mb-4">
              <BookOpen className="w-8 h-8 text-primary" />
              <h1 className="text-3xl sm:text-4xl font-bold">Peptide Research</h1>
            </div>
            <p className="text-base sm:text-lg font-medium text-muted-foreground max-w-3xl mx-auto px-2 sm:px-0">
              Comprehensive research articles covering peptide studies, clinical trials, mechanisms of action, and therapeutic applications backed by scientific evidence.
            </p>
          </div>

          {/* Search Bar */}
          <div className="mb-6 sm:mb-8">
            <div className="relative max-w-xl mx-auto">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-5 h-5" />
              <Input
                type="text"
                placeholder="Search research articles by title, content, or tags..."
                value={localSearch}
                onChange={(e) => setLocalSearch(e.target.value)}
                className="pl-10 h-12"
                data-testid="input-search-guides"
              />
            </div>
          </div>

          {/* Category Filters */}
          {categories.length > 0 && (
            <div className="mb-8 sm:mb-10">
              <div className="flex flex-wrap items-center justify-center gap-2 sm:gap-3">
                <Button
                  variant={selectedCategory === null ? "default" : "outline"}
                  size="sm"
                  onClick={() => handleCategoryChange(null)}
                  data-testid="button-filter-all"
                >
                  All Guides ({guidesResponse?.total || 0})
                </Button>
                {categories.map((category) => (
                  <Button
                    key={category}
                    variant={selectedCategory === category ? "default" : "outline"}
                    size="sm"
                    onClick={() => handleCategoryChange(category)}
                    data-testid={`button-filter-${category.toLowerCase().replace(/\s+/g, '-')}`}
                  >
                    {category} ({categoryCounts.get(category) || 0})
                  </Button>
                ))}
              </div>
            </div>
          )}

          {/* Guides Grid */}
          {isLoading ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-5 md:gap-6">
              {[...Array(12)].map((_, i) => (
                <Card key={i} className="h-full">
                  <div className="p-6">
                    <Skeleton className="h-5 w-20 mb-3" />
                    <Skeleton className="h-6 w-full mb-2" />
                    <Skeleton className="h-6 w-3/4 mb-4" />
                    <Skeleton className="h-4 w-full mb-2" />
                    <Skeleton className="h-4 w-5/6 mb-4" />
                    <Skeleton className="h-4 w-32" />
                  </div>
                </Card>
              ))}
            </div>
          ) : paginatedGuides.length === 0 ? (
            <div className="text-center py-16">
              <BookOpen className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-lg text-muted-foreground">
                {searchQuery || selectedCategory
                  ? "No guides found matching your criteria."
                  : "No guides available yet."}
              </p>
              {(searchQuery || selectedCategory) && (
                <Button 
                  variant="outline" 
                  className="mt-4"
                  onClick={() => {
                    setLocalSearch('');
                    setSearchQuery('');
                    setSelectedCategory(null);
                    setCurrentPage(1);
                    updateUrl('', null, 1);
                  }}
                  data-testid="button-clear-filters"
                >
                  Clear Filters
                </Button>
              )}
            </div>
          ) : (
            <>
              {/* Pagination Summary */}
              <div className="text-center mb-4">
                <p className="text-sm text-muted-foreground" data-testid="pagination-summary">
                  Showing {((currentPage - 1) * ITEMS_PER_PAGE) + 1}-{Math.min(currentPage * ITEMS_PER_PAGE, filteredGuides.length)} of {filteredGuides.length} guides
                </p>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 sm:gap-5 md:gap-6 mb-8">
                {paginatedGuides.map((guide) => (
                  <GuideCard key={guide.id} guide={guide} />
                ))}
              </div>

              {/* Pagination */}
              {totalFilteredPages > 1 && (
                <div className="flex justify-center">
                  <Pagination>
                    <PaginationContent>
                      <PaginationItem>
                        <PaginationPrevious 
                          onClick={() => currentPage > 1 && handlePageChange(currentPage - 1)}
                          className={currentPage === 1 ? "pointer-events-none opacity-50" : "cursor-pointer"}
                          data-testid="button-page-previous"
                        />
                      </PaginationItem>

                      {/* Page numbers */}
                      {[...Array(totalFilteredPages)].map((_, i) => {
                        const pageNum = i + 1;
                        // Show first, last, and pages around current
                        if (
                          pageNum === 1 ||
                          pageNum === totalFilteredPages ||
                          (pageNum >= currentPage - 1 && pageNum <= currentPage + 1)
                        ) {
                          return (
                            <PaginationItem key={pageNum}>
                              <PaginationLink
                                onClick={() => handlePageChange(pageNum)}
                                isActive={currentPage === pageNum}
                                className="cursor-pointer"
                                data-testid={`button-page-${pageNum}`}
                              >
                                {pageNum}
                              </PaginationLink>
                            </PaginationItem>
                          );
                        } else if (
                          pageNum === currentPage - 2 ||
                          pageNum === currentPage + 2
                        ) {
                          return (
                            <PaginationItem key={pageNum}>
                              <PaginationEllipsis />
                            </PaginationItem>
                          );
                        }
                        return null;
                      })}

                      <PaginationItem>
                        <PaginationNext 
                          onClick={() => currentPage < totalFilteredPages && handlePageChange(currentPage + 1)}
                          className={currentPage === totalFilteredPages ? "pointer-events-none opacity-50" : "cursor-pointer"}
                          data-testid="button-page-next"
                        />
                      </PaginationItem>
                    </PaginationContent>
                  </Pagination>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </>
  );
}

server/content-sanitizer.ts.content:
import DOMPurify from 'dompurify';
import { JSDOM } from 'jsdom';
import { marked } from 'marked';

// Create a window object for DOMPurify
const window = new JSDOM('').window;
const purify = DOMPurify(window);

// Get Node constants from the window
const Node = window.Node;

// Configure DOMPurify to allow safe HTML elements and attributes
const ALLOWED_TAGS = [
  'h1', 'h2', 'h3', 'h4', 'h5', 'h6',
  'p', 'br', 'strong', 'em', 'u', 's', 'code', 'pre',
  'ul', 'ol', 'li', 'a', 'img', 'blockquote',
  'table', 'thead', 'tbody', 'tr', 'th', 'td',
  'div', 'span', 'section', 'article'
];

const ALLOWED_ATTRIBUTES = {
  'a': ['href', 'target', 'rel', 'class', 'data-testid'],
  'img': ['src', 'alt', 'width', 'height'],
  '*': ['id', 'class']
};

// Product URL patterns for Shrine Peptides
const PRODUCT_URL_PATTERNS = [
  /shrinepeptides\.com\/product\//i,
  /shrine-peptides\.com\/product\//i,
  /\/product\/([A-Z0-9-]+)/i
];

// Common DOI and PMID patterns
const DOI_PATTERN = /10\.\d{4,}\/[-._;()\/:A-Za-z0-9]+/g;
const PMID_PATTERN = /PMID:\s*(\d+)/gi;
const PMCID_PATTERN = /PMC\d+/gi;

/**
 * Sanitizes and normalizes content, converting markdown to HTML if needed
 * @param content - The raw content (HTML or Markdown)
 * @param isMarkdown - Whether the content is in Markdown format (auto-detected if not specified)
 * @returns Sanitized and normalized HTML content
 */
export function sanitizeAndNormalizeContent(
  content: string, 
  isMarkdown?: boolean
): string {
  if (!content) {
    return '';
  }

  // Auto-detect markdown if not specified
  if (isMarkdown === undefined) {
    // Simple heuristics for markdown detection
    const markdownIndicators = [
      /^#{1,6}\s/m,        // Markdown headers
      /^\*\s/m,            // Bullet lists
      /^\d+\.\s/m,         // Numbered lists
      /\[.*?\]\(.*?\)/,    // Markdown links
      /\*\*.*?\*\*/,       // Bold text
      /\*.*?\*/,           // Italic text
      /^>\s/m              // Blockquotes
    ];

    isMarkdown = markdownIndicators.some(pattern => pattern.test(content));
  }

  // Convert markdown to HTML if needed
  let htmlContent = isMarkdown ? (marked.parse(content) as string) : content;

  // Create a DOM from the HTML for manipulation
  const dom = new JSDOM(htmlContent);
  const document = dom.window.document;

  // 1. Normalize heading hierarchy
  normalizeHeadings(document);

  // 2. Normalize lists (convert loose bullet points and numbered items)
  normalizeLists(document);

  // 3. Ensure paragraphs are properly wrapped
  normalizeParagraphs(document);

  // 4. Handle product links
  processProductLinks(document);

  // 5. Format citations and references
  formatCitations(document);

  // Get the normalized HTML
  const normalizedHTML = document.body.innerHTML;

  // Sanitize with DOMPurify - need to be more permissive with attributes
  const cleanHTML = purify.sanitize(normalizedHTML, {
    ALLOWED_TAGS,
    ALLOWED_ATTR: ['href', 'target', 'rel', 'class', 'data-testid', 'id', 'src', 'alt', 'width', 'height'],
    KEEP_CONTENT: true,
    ADD_TAGS: ['section', 'article'],
    ADD_ATTR: ['data-testid', 'target', 'rel']
  });

  return cleanHTML;
}

/**
 * Normalizes heading hierarchy to ensure proper structure
 */
function normalizeHeadings(document: Document): void {
  const headings = document.querySelectorAll('h1, h2, h3, h4, h5, h6');
  let lastLevel = 0;

  headings.forEach((heading) => {
    const currentLevel = parseInt(heading.tagName[1]);

    // Ensure headings don't skip levels
    if (lastLevel > 0 && currentLevel > lastLevel + 1) {
      const newLevel = lastLevel + 1;
      const newHeading = document.createElement(`h${newLevel}`);
      newHeading.innerHTML = heading.innerHTML;
      Array.from(heading.attributes).forEach(attr => {
        newHeading.setAttribute(attr.name, attr.value);
      });
      heading.parentNode?.replaceChild(newHeading, heading);
      lastLevel = newLevel;
    } else {
      lastLevel = currentLevel;
    }
  });
}

/**
 * Normalizes lists by converting loose bullet points and numbered items
 */
function normalizeLists(document: Document): void {
  const body = document.body;
  const walker = document.createTreeWalker(
    body,
    window.NodeFilter.SHOW_TEXT,
    null
  );

  const textNodes: Node[] = [];
  let node;
  while (node = walker.nextNode()) {
    textNodes.push(node);
  }

  textNodes.forEach(textNode => {
    const text = textNode.textContent || '';
    const lines = text.split('\n');

    const processedLines: (string | HTMLElement)[] = [];
    let currentList: HTMLElement | null = null;
    let listType: 'ul' | 'ol' | null = null;

    lines.forEach(line => {
      const trimmedLine = line.trim();

      // Check for bullet points
      const bulletMatch = trimmedLine.match(/^[\*\-‚Ä¢]\s+(.+)$/);
      // Check for numbered items
      const numberMatch = trimmedLine.match(/^\d+[\.\)]\s+(.+)$/);

      if (bulletMatch) {
        if (!currentList || listType !== 'ul') {
          if (currentList) processedLines.push(currentList);
          currentList = document.createElement('ul');
          listType = 'ul';
        }
        const li = document.createElement('li');
        li.textContent = bulletMatch[1];
        currentList.appendChild(li);
      } else if (numberMatch) {
        if (!currentList || listType !== 'ol') {
          if (currentList) processedLines.push(currentList);
          currentList = document.createElement('ol');
          listType = 'ol';
        }
        const li = document.createElement('li');
        li.textContent = numberMatch[1];
        currentList.appendChild(li);
      } else {
        if (currentList) {
          processedLines.push(currentList);
          currentList = null;
          listType = null;
        }
        if (trimmedLine) {
          processedLines.push(line);
        }
      }
    });

    if (currentList) {
      processedLines.push(currentList);
    }

    // Replace text node with processed content
    if (processedLines.length > 0 && textNode.parentNode) {
      const fragment = document.createDocumentFragment();
      processedLines.forEach((item, index) => {
        if (typeof item === 'string') {
          fragment.appendChild(document.createTextNode(item));
          if (index < processedLines.length - 1) {
            fragment.appendChild(document.createTextNode('\n'));
          }
        } else {
          fragment.appendChild(item);
        }
      });
      textNode.parentNode.replaceChild(fragment, textNode);
    }
  });
}

/**
 * Ensures paragraphs are properly wrapped in <p> tags
 */
function normalizeParagraphs(document: Document): void {
  const body = document.body;
  const childNodes = Array.from(body.childNodes);

  let currentParagraph: HTMLElement | null = null;
  const newChildren: Node[] = [];

  childNodes.forEach(node => {
    if (node.nodeType === Node.TEXT_NODE) {
      const text = (node.textContent || '').trim();
      if (text) {
        if (!currentParagraph) {
          currentParagraph = document.createElement('p');
          newChildren.push(currentParagraph);
        }
        currentParagraph.appendChild(document.createTextNode(text + ' '));
      } else if (currentParagraph && (node.textContent || '').includes('\n\n')) {
        // Double newline indicates paragraph break
        currentParagraph = null;
      }
    } else if (node.nodeType === Node.ELEMENT_NODE) {
      const element = node as HTMLElement;
      const blockTags = ['h1', 'h2', 'h3', 'h4', 'h5', 'h6', 'p', 'ul', 'ol', 'blockquote', 'table', 'div', 'section', 'article'];

      if (blockTags.includes(element.tagName.toLowerCase())) {
        currentParagraph = null;
        newChildren.push(node);
      } else {
        // Inline element
        if (!currentParagraph) {
          currentParagraph = document.createElement('p');
          newChildren.push(currentParagraph);
        }
        currentParagraph.appendChild(node);
      }
    }
  });

  // Replace body content with normalized content
  body.innerHTML = '';
  newChildren.forEach(child => body.appendChild(child));
}

/**
 * Processes product links to add special attributes
 */
function processProductLinks(document: Document): void {
  const links = document.querySelectorAll('a');

  links.forEach(link => {
    const href = link.getAttribute('href') || '';

    // Check if this is a product link
    const isProductLink = PRODUCT_URL_PATTERNS.some(pattern => pattern.test(href));

    if (isProductLink) {
      // Extract SKU from URL if possible
      const skuMatch = href.match(/\/([A-Z0-9-]+)(?:\?|#|$)/i);
      const sku = skuMatch ? skuMatch[1] : 'unknown';

      // Add special attributes
      link.setAttribute('class', 'product-link');
      link.setAttribute('data-testid', `link-product-${sku.toLowerCase()}`);
      link.setAttribute('rel', 'noopener');
      link.setAttribute('target', '_blank');
    }
  });
}

/**
 * Formats citations and references section
 */
function formatCitations(document: Document): void {
  // Find references section
  const headings = document.querySelectorAll('h1, h2, h3, h4, h5, h6');
  let referencesSection: HTMLElement | null = null;

  headings.forEach(heading => {
    if (/references?|citations?|bibliography/i.test(heading.textContent || '')) {
      referencesSection = heading as HTMLElement;
      referencesSection.setAttribute('id', 'references');
    }
  });

  if (referencesSection) {
    // Find the content following the references heading
    let nextElement: Element | null = (referencesSection as HTMLElement).nextElementSibling;

    while (nextElement) {
      // Check if it's a list that needs formatting
      if (nextElement.tagName === 'UL') {
        // Convert UL to OL for references
        const ol = document.createElement('ol');
        const children = Array.from(nextElement.children) as Element[];
        children.forEach((child) => {
          ol.appendChild(child.cloneNode(true));
        });
        nextElement.parentNode?.replaceChild(ol, nextElement);
        nextElement = ol;
      }

      // Process reference text for DOIs and PMIDs
      if (nextElement.tagName === 'OL' || nextElement.tagName === 'UL') {
        const listItems = nextElement.querySelectorAll('li');
        listItems.forEach((li: Element) => {
          let html = li.innerHTML;

          // Convert DOIs to links
          html = html.replace(DOI_PATTERN, (match: string) => {
            return `<a href="https://doi.org/${match}" target="_blank" rel="noopener">${match}</a>`;
          });

          // Convert PMIDs to links
          html = html.replace(PMID_PATTERN, (match: string, pmid: string) => {
            return `<a href="https://pubmed.ncbi.nlm.nih.gov/${pmid}/" target="_blank" rel="noopener">PMID: ${pmid}</a>`;
          });

          // Convert PMCIDs to links
          html = html.replace(PMCID_PATTERN, (match: string) => {
            const pmcid = match.replace('PMC', '');
            return `<a href="https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/" target="_blank" rel="noopener">${match}</a>`;
          });

          li.innerHTML = html;
        });
      }

      // Check if we've hit another major section
      if (nextElement.tagName.match(/^H[1-6]$/)) {
        break;
      }

      nextElement = nextElement.nextElementSibling;
    }
  }

  // Also process inline citations throughout the document
  const allTextElements = document.querySelectorAll('p, li, td, th');
  allTextElements.forEach(element => {
    let html = element.innerHTML;

    // Convert DOIs to links
    html = html.replace(DOI_PATTERN, (match: string) => {
      // Check if already in a link
      if (!element.querySelector(`a[href*="${match}"]`)) {
        return `<a href="https://doi.org/${match}" target="_blank" rel="noopener">${match}</a>`;
      }
      return match;
    });

    // Convert PMIDs to links
    html = html.replace(PMID_PATTERN, (match: string, pmid: string) => {
      // Check if already in a link
      if (!element.querySelector(`a[href*="${pmid}"]`)) {
        return `<a href="https://pubmed.ncbi.nlm.nih.gov/${pmid}/" target="_blank" rel="noopener">PMID: ${pmid}</a>`;
      }
      return match;
    });

    // Convert PMCIDs to links
    html = html.replace(PMCID_PATTERN, (match) => {
      const pmcid = match.replace('PMC', '');
      // Check if already in a link
      if (!element.querySelector(`a[href*="PMC${pmcid}"]`)) {
        return `<a href="https://www.ncbi.nlm.nih.gov/pmc/articles/PMC${pmcid}/" target="_blank" rel="noopener">${match}</a>`;
      }
      return match;
    });

    element.innerHTML = html;
  });
}

// Export helper function for testing
export function detectContentType(content: string): 'markdown' | 'html' {
  const markdownIndicators = [
    /^#{1,6}\s/m,        // Markdown headers
    /^\*\s/m,            // Bullet lists
    /^\d+\.\s/m,         // Numbered lists
    /\[.*?\]\(.*?\)/,    // Markdown links
    /\*\*.*?\*\*/,       // Bold text
    /^>\s/m              // Blockquotes
  ];

  const htmlIndicators = [
    /<[^>]+>/,           // HTML tags
    /&[a-z]+;/i          // HTML entities
  ];

  const markdownScore = markdownIndicators.filter(pattern => pattern.test(content)).length;
  const htmlScore = htmlIndicators.filter(pattern => pattern.test(content)).length;

  return markdownScore > htmlScore ? 'markdown' : 'html';
}

server/normalize-all-guides.ts.content:
import { db } from './db';
import { guides } from '@shared/schema';
import { sanitizeAndNormalizeContent } from './content-sanitizer';
import { eq } from 'drizzle-orm';

async function normalizeAllGuides() {
  console.log('Starting guide normalization process...');

  try {
    // Fetch all guides from the database
    const allGuides = await db.select().from(guides);
    const totalGuides = allGuides.length;

    console.log(`Found ${totalGuides} guides to normalize`);

    if (totalGuides === 0) {
      console.log('No guides found in the database');
      return;
    }

    let successCount = 0;
    let errorCount = 0;
    const errors: Array<{ id: string; title: string; error: string }> = [];

    // Process each guide
    for (let i = 0; i < allGuides.length; i++) {
      const guide = allGuides[i];
      const progress = i + 1;

      try {
        console.log(`Normalizing guide ${progress}/${totalGuides}: "${guide.title}"`);

        // Sanitize and normalize the content
        const normalizedContent = sanitizeAndNormalizeContent(guide.content, true);

        // Update the guide in the database with normalized content
        await db
          .update(guides)
          .set({ 
            content: normalizedContent,
            lastUpdated: new Date() 
          })
          .where(eq(guides.id, guide.id));

        successCount++;
        console.log(`  ‚úì Successfully normalized guide: "${guide.title}"`);
      } catch (error) {
        errorCount++;
        const errorMessage = error instanceof Error ? error.message : 'Unknown error';
        errors.push({
          id: guide.id,
          title: guide.title,
          error: errorMessage
        });
        console.error(`  ‚úó Error normalizing guide "${guide.title}": ${errorMessage}`);
      }
    }

    // Print summary
    console.log('\n' + '='.repeat(60));
    console.log('NORMALIZATION COMPLETE');
    console.log('='.repeat(60));
    console.log(`Total guides processed: ${totalGuides}`);
    console.log(`Successfully normalized: ${successCount}`);
    console.log(`Errors encountered: ${errorCount}`);

    if (errors.length > 0) {
